package hello;

public class Feature {

	private String HDD;
	private int RAM;
	
	public Feature() {
		// TODO Auto-generated constructor stub
	}
	
	public Feature(String HDD, int RAM) {
		this.HDD = HDD;
		this.RAM = RAM;
		
	}

	public String getHDD() {
		return HDD;
	}

	public void setHDD(String hDD) {
		HDD = hDD;
	}

	public int getRAM() {
		return RAM;
	}

	public void setRAM(int rAM) {
		RAM = rAM;
	}
	
	
}
